// Utilisation de jQuery dans un mode strict
(function($) {
    "use strict";
  
    // Gestion du clic sur les éléments avec les ID #sidebarToggle et #sidebarToggleTop
    $("#sidebarToggle, #sidebarToggleTop").on("click", function(e) {
      $("body").toggleClass("sidebar-toggled");
      $(".sidebar").toggleClass("toggled");
      if ($(".sidebar").hasClass("toggled")) {
        $(".sidebar .collapse").collapse("hide");
      }
    });
  
    // Gestion du redimensionnement de la fenêtre
    $(window).resize(function() {
      if ($(window).width() < 768) {
        $(".sidebar .collapse").collapse("hide");
      }
  
      // Toggle de la barre latérale lorsque la fenêtre est redimensionnée en dessous de 480 pixels
      if ($(window).width() < 480 && !$(".sidebar").hasClass("toggled")) {
        $("body").addClass("sidebar-toggled");
        $(".sidebar").addClass("toggled");
        $(".sidebar .collapse").collapse("hide");
      }
    });
  
    // Empêche le défilement du contenu principal lorsque la barre latérale fixe est survolée
    $("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function(e) {
      if ($(window).width() > 768) {
        var event = e.originalEvent,
          delta = event.wheelDelta || -event.detail;
        this.scrollTop += (delta < 0 ? 1 : -1) * 30;
        e.preventDefault();
      }
    });
  
    // Affiche ou masque le bouton de retour en haut en fonction du défilement
    $(document).on("scroll", function() {
      var scrollDistance = $(this).scrollTop();
      if (scrollDistance > 100) {
        $(".scroll-to-top").fadeIn();
      } else {
        $(".scroll-to-top").fadeOut();
      }
    });
  
    // Défilement fluide vers le haut lorsqu'un lien avec la classe scroll-to-top est cliqué
    $(document).on("click", "a.scroll-to-top", function(e) {
      var $anchor = $(this);
      $("html, body").stop().animate({
        scrollTop: $($anchor.attr("href")).offset().top
      }, 1000, "easeInOutExpo");
      e.preventDefault();
    });
  
  })(jQuery);
  